import numpy as npimport matplotlib.pyplot as plt#def generujTabliceGeometrii(x_p, x_k, n):#    temp = (x_k - x_p) / (n - 1)#    wezly = np.array([x_p, x_k])##    for i in range(1, n, 1):#        wezly = np.block([wezly, i * temp + x_p])#    #    elementy = np.array([])##    return wezly##wezly = np.array([0, 1, 0.5, 0.75 ])elementy = np.array([[1, 3],                     [4, 2],                     [3, 4]])twb_L = 'D'twb_R = 'D'wwb_L = 0wwb_R = 1n = 100#def Inicjalizacja_paramatrow_sterujacych(przedzial,war_brzeg)def GenerujTabliceGeometrii(x_p,x_k,n):        val = (x_k-x_p)/(n-1)    wezly = np.array([x_p])        for i in range(1,n,1):        wezly = np.block([wezly, i * val + x_p])        elementy = np.zeros((n-1,2))       for i in range(0, n-1, 1):        elementy[i][0] = i+1        elementy[i][1] = i+2        return wezly,elementydef Rysuj_geometrie(wezly):    n = len(wezly)    tab=np.zeros((n))    l1 = plt.plot(wezly,tab)    for i in range(0,n,1):        plt.text(wezly[i], -0.01, str(i+1))        plt.text(wezly[i], 0, "|")        plt.text(wezly[i]/2+wezly[i]/2, 0.005 ,str(i+1))    plt.show()        Wezly, Elementy = GenerujTabliceGeometrii(0,1,4)Rysuj_geometrie(Wezly)print('Liczba wezlow: ', len(Wezly))print('Tablica wezlow: ', Wezly)print('Tablica elementow: ', Elementy)