import numpy as npimport matplotlib.pyplot as pltdef generujTabliceGeometrii(x_p, x_k, n):    temp = (x_k - x_p) / (n - 1)    wezly = np.array([x_p, x_k])    for i in range(1, n-1, 1):        wezly = np.block([wezly, i * temp + x_p])        elementy = np.array([])    n = n-1    return wezlyprint(generujTabliceGeometrii(0,1,7))